<script>
/* import axios from "axios"; */
import nearestColor from "nearest-color";
import colorNameList from "color-name-list";
import invert from "invert-color";
export default {
  data() {
    return {
      colorOne: "",
      colorTwo: "",
      invColorOne: "",
      invColorTwo: "",
      deg: "",
      colName1: "",
      colName2: "",
      url: "",
      cssText: "",
      showFooter: false,
    };
  },
  methods: {
    rndColor() {
      const { randomHexColor } = require("random-hex-color-generator");
      const rndColor1 = randomHexColor();
      const rndColor2 = randomHexColor();
      const rndDeg = Math.floor(Math.random() * 360);
      this.colorOne = rndColor1;
      this.colorTwo = rndColor2;
      this.invColorOne = invert(rndColor1);
      this.invColorTwo = invert(rndColor2);
      this.deg = rndDeg + "deg";
      this.url =
        "https://api.color.pizza/v1/" +
        rndColor1.slice(1) +
        "," +
        rndColor2.slice(1);

      this.cssText =
        "background: linear-gradient(to right," +
        rndColor1 +
        "," +
        rndColor2 +
        ");";

      this.getNearest();
      this.showFooter = true;
      return { rndColor1, rndColor2 };
    },
    rndTrig() {
      setInterval(this.rndColor, 2000);
      this.getColornames();
      this.getInverted();
    },
    /*     async getColornames() {
      const response = await axios({
        url: this.url,
      });

      this.colName1 = response.data.colors[0].name;
      this.colName1 = response.data.colors[1].name;
      if (!response.data) {
        return;
      }
    }, */
    getNearest() {
      // nearestColor need objects {name => hex} as input
      const colors = colorNameList.reduce(
        (o, { name, hex }) => Object.assign(o, { [name]: hex }),
        {}
      );
      const nearest = nearestColor.from(colors);
      const nearestOne = nearest(this.colorOne);
      const nearestTwo = nearest(this.colorTwo);
      this.colName1 = nearestOne.name;
      this.colName2 = nearestTwo.name;
    },
    getInverted() {
      const invert1 = invert(this.colorOne);
      console.log(invert1 + "hei");
    },
  },
  mounted() {
    this.colorOne = "#808080";
    this.colorTwo = "#808080";
    this.cssText = "hello. click the button above to generate gradient.";
  },
};
</script>

